CREATE TABLE IF NOT EXISTS civilization (
	id_civilization	integer NOT NULL PRIMARY KEY AUTOINCREMENT,
	name	varchar NOT NULL,
	expansion	varchar NOT NULL,
	characteristic_civilization	varchar NOT NULL,
	team_bonus	varchar NOT NULL,
	bonus_civilization	varchar NOT NULL
);

CREATE TABLE IF NOT EXISTS structure_type (
	id_structure_type	integer NOT NULL PRIMARY KEY AUTOINCREMENT,
	name	varchar NOT NULL
);

CREATE TABLE IF NOT EXISTS structure_stat (
	id_structure_stat	integer NOT NULL PRIMARY KEY AUTOINCREMENT,
	hit_points	integer,
	rate_of_fire	float,
	range	float,
	attack	integer,
	melee_armor	integer,
	pierce_armor	integer,
	line_of_sight	integer,
	garrison	integer,
	structure	integer NOT NULL,
	age	integer NOT NULL,
	FOREIGN KEY(structure) REFERENCES structure(id_structure) on delete cascade,
	FOREIGN KEY(age) REFERENCES age(id_age) on delete cascade
);

CREATE TABLE IF NOT EXISTS structure (
	id_structure	integer NOT NULL PRIMARY KEY AUTOINCREMENT,
	name	varchar NOT NULL,
	expansion	varchar,
	wood	integer,
	stone	integer,
	gold	integer,
	build_time	integer,
	type	integer NOT NULL,
	age	integer NOT NULL,
	FOREIGN KEY(age) REFERENCES age(id_age) on delete cascade,
	FOREIGN KEY(type) REFERENCES structure_type(id_structure_type) on delete cascade
);

CREATE TABLE IF NOT EXISTS age (
	id_age	integer NOT NULL PRIMARY KEY AUTOINCREMENT,
	name	varchar NOT NULL
);

CREATE TABLE IF NOT EXISTS technology (
	id_technology	integer NOT NULL PRIMARY KEY AUTOINCREMENT,
	name	varchar,
	description	varchar,
	expansion	varchar,
	food	integer,
	wood	integer,
	stone	integer,
	gold	integer,
	build_time	integer,
	develops_in	integer NOT NULL,
	civilization	integer,
	age	integer NOT NULL,
	FOREIGN KEY(develops_in) REFERENCES structure(id_structure) on delete cascade,
	FOREIGN KEY(civilization) REFERENCES civilization(id_civilization) on delete cascade,
	FOREIGN KEY(age) REFERENCES age(id_age) on delete cascade
);

CREATE TABLE IF NOT EXISTS unit (
	id_unit	integer NOT NULL PRIMARY KEY AUTOINCREMENT,
	name	varchar,
	expansion	varchar,
	build_time	integer,
	hit_points	integer,
	rate_of_fire	float,
	frame_delay	integer,
	range	float,
	accuracy	float,
	projectile_speed	float,
	melee_armor	integer,
	pierce_armor	integer,
	speed	float,
	line_of_sight	integer,
	garrison	integer,
	blast_radius	float,
	attack_bonus	varchar,
	wood	integer,
	gold	integer,
	stone	integer,
	food	integer,
	attack	integer,
	created_in	integer NOT NULL,
	civilization	integer,
	age	integer NOT NULL,
	FOREIGN KEY(created_in) REFERENCES structure(id_structure) on delete cascade,
	FOREIGN KEY(civilization) REFERENCES civilization(id_civilization) on delete cascade,
	FOREIGN KEY(age) REFERENCES age(id_age) on delete cascade
);

